	/**
	 * Two pointers, sliding window
	 */
	public int lengthOfLongestSubstring2(String s) {
		Map<Character, Integer> map = new HashMap<>();
		int max = 0;
		for (int l = 0, r = 0; r < s.length(); r++) {
			char sr = s.charAt(r);
			if (map.containsKey(sr)) {
				// sliding window until l pass the repeating character
				int end = map.get(sr); // index of repeating character
				while (l <= end)
					map.remove(s.charAt(l++));
			}

			map.put(sr, r);
			int length = r - l + 1;
			max = length > max ? length : max;
		}
		return max;
	}

	/**
	 * Naive Dynamic Programming Version
	 * Status dp[i] is the longest length of the substring
	 * end at i
	 * 
	 * if(findRepeat)
	 * dp[i] = i - indexOfRepeat
	 * else
	 * dp[i] = dp[i-1] + 1
	 * 
	 * if we calculated dp[i] from 0 to n,
	 * then we can calculate the global max length by a
	 * simple dp[i] traversal.
	 * 
	 * maxLen = max(maxLen, dp[i-1]+1), maxLen is the
	 * global max length of Non-repeat Longest subString
	 * 
	 */
	public static int dp(String str) {
		int len = str.length();
		int[] longestLenAt = new int[len];
		longestLenAt[0] = 1;
		int maxLen = 1;
		int start = 0;
		// longestLenAt[i] is the longest length of substring end at i
		for (int i = 1; i < len; i++) {
			char current = str.charAt(i);
			// search for repeat
			boolean findRepeat = false;
			for (int j = i - 1; j >= start; j--) {
				if (str.charAt(j) == current) {
					findRepeat = true;
					start = j + 1;
					longestLenAt[i] = i - j;
					break;
				}
			}

			if (!findRepeat)
				longestLenAt[i] = longestLenAt[i - 1] + 1;

			maxLen = Math.max(maxLen, longestLenAt[i]);
		}
		return maxLen;
	}

	/**
	 * Advanced version of above dp version, dp + hash
	 * Instead of searching for repeat char by traversal,
	 * we use an int[256] to save the position of all
	 * characters of input String str.
	 * 
	 * Important:
	 * We keep track of the position of start char which
	 * is also the repeated char, we use start position to
	 * decide the current char is repeated or not:
	 * if it is repeated, then it must be in the right side of
	 * start char: position[current] >= start
	 * if it is not repeated, it must be in the left side of
	 * start char: position[current] < start
	 * 
	 * @param str
	 * @return
	 */
	public static int dp_hash(String str) {
		int length = str.length();
		if (length == 0) {
			return 0;
		}

		// positions of all characters in String s
		int[] postions = new int[256];
		Arrays.fill(postions, -1);
		int maxLen = 1;
		int start = 0;
		postions[str.charAt(0)] = 0;

		for (int i = 1; i < length; i++) {
			char current = str.charAt(i);
			if (postions[current] >= start) {
				start = postions[current] + 1;
			}
			maxLen = Math.max(maxLen, i - start + 1);
			postions[current] = i;
		}
		return maxLen;
	}

	/**
	 * Same solution as dp_hash, just another way to write it
	 */
	public static int lengthOfLongestSubstring(String s) {
		if (s.length() == 0)
			return 0;
		int[] exists = new int[128];
		Arrays.fill(exists, -1);
		int longest = 1;
		int length = 1;
		exists[s.charAt(0)] = 0;
		int start = 0;
		for (int i = 1; i < s.length(); i++) {
			int repeatIdx = exists[s.charAt(i)];
			if (repeatIdx >= start) { // find repeat
				length = i - repeatIdx;
				start = repeatIdx + 1;
			} else
				length++;

			exists[s.charAt(i)] = i;
			longest = length > longest ? length : longest;
		}
		return longest;
	}

